mapper.select:
SELECT COMMODITY_ID commodityId, CATEGORY category, COMMODITY_NAME commodityName, AD_WORD adWord, AD_URL adUrl, COMMODITY_PRICE commodityPrice, PAY_URL payUrl, AD_DURATION adDuration, AD_PRICE adPrice, ADDR addr, SPOKESPERSON_ID spokesPersonId, MERCHANT_ID merchantId, DATE_FORMAT(APPLICATION_DATE ,'%Y-%m-%d %H:%i:%s') applicationTime, DATE_FORMAT(START_DATE ,'%Y-%m-%d %H:%i:%s') startDate, DATE_FORMAT(END_DATE ,'%Y-%m-%d %H:%i:%s') endDate, STATUS status FROM yuyue_commodity where 1=1 and
merchantId = #{merchantId}
spokesPersonId = #{spokesPersonId}
spokesPersonId = #{spokesPersonId} and merchantId = #{merchantId}
