mapper.select:
SELECT *,DATE_FORMAT(COMPLETE_TIME ,'%Y-%m-%d %H:%i:%s')completeTime
 FROM yuyue_order b WHERE 1 = 1 and tradeType like  'SC%'

    and  b.merchantId = #{consumerId}



    and  status = #{status}

order  by create_time desc

SELECT b.*,DATE_FORMAT(b.COMPLETE_TIME ,'%Y-%m-%d %H:%i:%s') completeTime ,DATE_FORMAT(b.CREATE_TIME ,'%Y-%m-%d %H:%i:%s') createTime FROM yuyue_change_money b where 1=1
and b.id = #{id}
order by b.COMPLETE_TIME desc LIMIT 1

SELECT d.merchant_id merchantId FROM yuyue_order b,yuyue_mall_order_item c,yuyue_mall_shop d WHERE 1=1 AND c.shop_id = d.shop_id
and b.id = #{id} and c.order_id = #{id}
GROUP BY d.merchant_id order by d.create_time desc

SELECT b.status FROM yuyue_order b WHERE 1=1
and b.id = #{orderId}
order by b.create_time desc limit 1

SELECT (SELECT c.USER_NICK_NAME FROM yuyue_merchant c WHERE c.ID = b.merchantId) yiName, (SELECT c.USER_NICK_NAME FROM yuyue_merchant c WHERE c.ID = b.sourceId) sourceName, (SELECT c.HEADP_URL FROM yuyue_merchant c WHERE c.ID = b.merchantId) headpUrl, b.merchantId,b.changeNo,b.tradeType,b.money,b.`status`,b.note, DATE_FORMAT(b.COMPLETE_TIME ,'%Y-%m-%d %H:%i:%s') completeTime FROM yuyue_change_money b WHERE 1=1
and b.videoId = #{videoId}
and b.tradeType like concat('%', #{tradeType}, '%')
and b.merchantId = #{userId}
order by b.COMPLETE_TIME desc LIMIT #{begin},#{size}
